waypoints.waypoint:
__WPL:setDirection (WPT_FORWARD);
if complete then
	print ('\a'); yrest (5000);
	loadPaths ('/TQ_return')
end

complete, numTqCards = dayliesComplete (numTqCards); while not complete do if getQuestStatus (questID)=='not accepted' then player:target_NPC (questNPC) AcceptQuestByName (questID) end
while getQuestStatus (questID)=='incomplete' do if inventory:itemTotalCount (egg)<=0 then player:target_NPC (questNPC) sendMacro("ChoiceOption(1)") yrest (1000) end while inventory:itemTotalCount (egg)>0 do player:target_NPC (122170) Attack () yrest (5000) if lastKey==nil or lastKey==settings.hotkeys.MOVE_BACKWARD.key then lastKey = settings.hotkeys.MOVE_FORWARD.key else lastKey = settings.hotkeys.MOVE_BACKWARD.key end keyboardPress (lastKey) yrest (200) keyboardRelease (lastKey) yrest (500) inventory:update () end end
if getQuestStatus (questID)=='complete' then player:target_NPC (questNPC) CompleteQuestByName (questID) end complete, numTqCards = dayliesComplete (numTqCards); end __WPL:setDirection (WPT_BACKWARD);
