-- Variables for onClientCrash local currAcc = RoMScript("LogID"); local currChar = RoMScript("CHARACTER_SELECT.selectedIndex"); local dirPath = string.gsub( getExecutionPath()," ","\" \"" ); local currPath = __WPL.FileName; local Clientlink = TierTwinkClient; -- specify you client shortcut links name if it not contains *client* cprintf(cli.white, "Current Acc: %s, Char: %s, Path: %s, dirPath: %s, Client: %s\n\n", currAcc, currChar, currPath, dirPath, Clientlink) -- onClientCrash function onClientCrash() if RestartAfterAllError then killClient() else yrest(5000) end if( not windowValid(getAttachedHwnd()) or RestartAfterAllError ) then -- write Crash Log local filename = ""..dirPath.."/logs/CrashLog.log"; local file, err = io.open(filename, "a+"); if file then file:write("Account: "..currAcc.." \tName: " ..string.format("%-10s",player.Name ).." \tDate: "..os.date().." \tPath: "..currPath.." \n") file:close() end -- Restart client and Bot os.execute("START "..dirPath.."/../../micromacro.exe "..dirPath.."/login acc:"..currAcc.." char:"..currChar.." client:"..Clientlink.." path:"..currPath.."") --os.exit() --elseif( TryRestartBot == 0 ) then -- Restart Bot --include("bot.lua") --TryRestartBot = 1 end end; -- check Clientlink if( Clientlink ) then atError(onClientCrash) end;
-- Start Crash Window Killer local name, version = "Crash Window Killer", "v0.1" local windowname = name.." ["..version.."] started." local crashwinkiller = findWindow(windowname) if( crashwinkiller == 0 ) then local a,b,c = os.execute("START "..dirPath.."/../../micromacro.exe "..dirPath.."/CrashWindowKiller.lua") if not a then error("Trouble executing file. Values returned were: "..(a or "nil")..", "..(b or "nil")..", "..(c or "nil")) end end;
